var $=function(a){$.dom=[].slice.apply(document.querySelectorAll(a));return $};$.html=function(a){$.dom.forEach(function(b){b.innerHTML=a});return $};$.append=function(a){$.dom.forEach(function(b){b.insertAdjacentHTML("beforeEnd",a)});return $};$.prepend=function(a){$.dom.forEach(function(b){b.insertAdjacentHTML("afterBegin",a)});return $};$.css=function(a){$.dom.forEach(function(b){b.style.cssText+=";"+a});return $};